import React, { useEffect, useState, useCallback } from 'react';

import Tasks from './components/Tasks/Tasks';
import NewTask from './components/NewTask/NewTask';
import useHttp from './hooks/use-http';

function App() {

  const [tasks, setTasks] = useState([]);

  const transformTasks = useCallback(taksObj => {
    const loadedTasks = []

    for (const taskKey in taksObj) {
      loadedTasks.push({ id: taskKey, text: taksObj[taskKey].text })
    }
    setTasks(loadedTasks)
  }, [])

  const { isLoading, error, sendRequest: fetchTasks } = useHttp(
    {
      url: 'https://react-http-7d180-default-rtdb.europe-west1.firebasedatabase.app/tasks.json'
    }, transformTasks
  );

  useEffect(() => {
    fetchTasks()
  }, []);

  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };

  return (
    <React.Fragment>
      <NewTask onAddTask={taskAddHandler} />
      <Tasks
        items={tasks}
        loading={isLoading}
        error={error}
        onFetch={fetchTasks}
      />
    </React.Fragment>
  );
}

export default App;
