  const [tasks, setTasks] = useState([]);

  useHttp(
    {
      url: 'https://react-http-7d180-default-rtdb.europe-west1.firebasedatabase.app/tasks.json'
    },
    {
      method: 'POST',
      body: JSON.stringify({ text: tasks.text }),
      headers: {
        'Content-Type': 'application/json',
      },
    }
  );

